"use strict";plateau.style.width="100%";var numCard=0,card=["c1","c2","c3","c4","c5","c6"],dé=document.getElementById("de"),qstn=document.getElementsByClassName("qust"),abc=document.getElementsByClassName("abc"),buttonRep=document.getElementsByClassName("buttonRep"),buttonJOK=document.getElementsByClassName("JOKER");function lancer(){numCard=Math.floor(Math.random()*Math.floor(6)),console.log(numCard),document.getElementById(card[numCard]).style.transform="translate(0%)",dé.style.display="none",qstn[numCard].innerHTML="La question ?",abc[numCard].innerText="A/B/C"}function reponse(){buttonRep[numCard].onclick=nextTour(),alert("Ici la réponse !"),dé.style.display="block",resetOnclick()}function nextTour(){for(var e=0;e<document.getElementsByClassName("c_left").length;e++)document.getElementsByClassName("c_left")[e].style.transform="translate(-90%)";for(var t=0;t<document.getElementsByClassName("c_right").length;t++)document.getElementsByClassName("c_right")[t].style.transform="translate(90%)"}function joker(){if(confirm("Etes vous sur de vouloir utiliser le JOKER ? Il n'est disponible qu'une fois par partie !"))for(var e=0;e<buttonJOK.length;e++)buttonJOK[e].style.display="none"}function resetOnclick(){buttonRep[numCard].setAttribute("onclick","reponse()")}